document.addEventListener('DOMContentLoaded', async ()=>{
  const token = localStorage.getItem('auth_token');
  if(!token){
    // não logado -> voltar para login
    alert('Você precisa fazer login');
    window.location.href = '/';
    return;
  }
  try{
    const res = await fetch('/auth/me', { headers: { 'Authorization': 'Bearer '+token }});
    const data = await res.json();
    if(!res.ok){
      alert(data.error || 'Sessão inválida. Faça login novamente.');
      localStorage.removeItem('auth_token');
      window.location.href = '/';
      return;
    }
    document.getElementById('username').textContent = data.username || 'Usuário';
  }catch(e){
    alert('Erro ao validar sessão: '+e.message);
    localStorage.removeItem('auth_token');
    window.location.href = '/';
  }

  const btnLogout = document.getElementById('btn-logout');
  if(btnLogout){
    btnLogout.addEventListener('click', ()=>{
      localStorage.removeItem('auth_token');
      window.location.href = '/';
    });
  }
});

document.addEventListener('DOMContentLoaded', async ()=>{
  const token = localStorage.getItem('auth_token');
  if(!token){
    alert('Você precisa fazer login');
    window.location.href = '/';
    return;
  }

  // Validar sessão
  try{
    const res = await fetch('/auth/me', { headers: { 'Authorization': 'Bearer '+token }});
    const data = await res.json();
    if(!res.ok){
      alert(data.error || 'Sessão inválida. Faça login novamente.');
      localStorage.removeItem('auth_token');
      window.location.href = '/';
      return;
    }
    document.getElementById('username').textContent = data.username || 'Usuário';
  }catch(e){
    alert('Erro ao validar sessão: '+e.message);
    localStorage.removeItem('auth_token');
    window.location.href = '/';
    return;
  }

  // Logout
  const btnLogout = document.getElementById('btn-logout');
  if(btnLogout){
    btnLogout.addEventListener('click', ()=>{
      localStorage.removeItem('auth_token');
      window.location.href = '/';
    });
  }

  // Elementos da página
  const searchInput = document.getElementById('search-input');
  const btnSearch = document.getElementById('btn-search');
  const loading = document.getElementById('loading');
  const resultsContainer = document.getElementById('results-container');
  const bookModal = document.getElementById('book-modal');
  const closeModal = document.querySelector('.close-modal');

  // Função para buscar livros
  async function searchBooks(query) {
    if(!query || query.trim() === ''){
      alert('Por favor, digite o nome de um livro para buscar');
      return;
    }

    loading.style.display = 'block';
    resultsContainer.innerHTML = '';

    try{
      const res = await fetch('/api/books', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': 'Bearer ' + token
        },
        body: JSON.stringify({ findBook: query })
      });

      const data = await res.json();

      if(!res.ok){
        throw new Error(data.error || 'Erro ao buscar livros');
      }

      displayBooks(data);
    }catch(e){
      resultsContainer.innerHTML = `
        <div class="error-message">
          <h3>❌ Erro na busca</h3>
          <p>${e.message}</p>
        </div>
      `;
    }finally{
      loading.style.display = 'none';
    }
  }

  // Função para exibir lista de livros
  function displayBooks(books) {
    if(!books || books.length === 0){
      resultsContainer.innerHTML = `
        <div class="no-results">
          <h3> Nenhum livro encontrado</h3>
          <p>Tente buscar com outros termos</p>
        </div>
      `;
      return;
    }

    resultsContainer.innerHTML = '<div class="books-grid"></div>';
    const grid = resultsContainer.querySelector('.books-grid');

    books.forEach(book => {
      const bookCard = createBookCard(book);
      grid.appendChild(bookCard);
    });
  }

  // Função para criar card de livro
  function createBookCard(book) {
    const card = document.createElement('div');
    card.className = 'book-card';

    const thumbnail = book.thumbnail || '/static/images/no-cover.png';
    const title = book.title || 'Título não disponível';
    const authors = book.authors && book.authors.length > 0 
      ? book.authors.join(', ') 
      : 'Autor desconhecido';
    const description = book.description 
      ? (book.description.substring(0, 150) + '...') 
      : 'Sem descrição disponível';

    card.innerHTML = `
      <div class="book-thumbnail">
        <img src="${thumbnail}" alt="${title}" onerror="this.src='/static/images/no-cover.png'">
      </div>
      <div class="book-info">
        <h3 class="book-title">${title}</h3>
        <p class="book-authors">${authors}</p>
        <p class="book-description">${description}</p>
        <button class="btn-details" data-id="${book.id}">Ver Detalhes</button>
      </div>
    `;

    const btnDetails = card.querySelector('.btn-details');
    btnDetails.addEventListener('click', () => loadBookDetails(book.id));

    return card;
  }

  // Função para carregar detalhes do livro
  async function loadBookDetails(bookId) {
    const bookDetails = document.getElementById('book-details');
    bookDetails.innerHTML = '<div class="loading"><div class="spinner"></div><p>Carregando detalhes...</p></div>';
    bookModal.style.display = 'flex';

    try{
      const res = await fetch(`/api/books/${bookId}`, {
        method: 'GET',
        headers: {
          'Authorization': 'Bearer ' + token
        }
      });

      const data = await res.json();

      if(!res.ok){
        throw new Error(data.error || 'Erro ao carregar detalhes do livro');
      }

      displayBookDetails(data);
    }catch(e){
      bookDetails.innerHTML = `
        <div class="error-message">
          <h3>❌ Erro</h3>
          <p>${e.message}</p>
        </div>
      `;
    }
  }

  // Função para exibir detalhes completos do livro
  function displayBookDetails(book) {
    const bookDetails = document.getElementById('book-details');

    const thumbnail = book.thumbnail || '/static/images/no-cover.png';
    const title = book.title || 'Título não disponível';
    const authors = book.authors && book.authors.length > 0 
      ? book.authors.join(', ') 
      : 'Autor desconhecido';
    const publisher = book.publisher || 'Editora desconhecida';
    const publishedDate = book.publishedDate || 'Data não disponível';
    const pageCount = book.pageCount || 'N/A';
    const categories = book.categories && book.categories.length > 0
      ? book.categories.join(', ')
      : 'Sem categoria';
    const language = book.language || 'N/A';
    const description = book.description || 'Sem descrição disponível';
    const previewLink = book.previewLink || '#';
    const infoLink = book.infoLink || '#';

    bookDetails.innerHTML = `
      <div class="book-detail-header">
        <img src="${thumbnail}" alt="${title}" class="book-detail-image" onerror="this.src='/static/images/no-cover.png'">
        <div class="book-detail-info">
          <h2>${title}</h2>
          <p class="detail-authors"><strong>Autor(es):</strong> ${authors}</p>
          <p><strong>Editora:</strong> ${publisher}</p>
          <p><strong>Data de Publicação:</strong> ${publishedDate}</p>
          <p><strong>Páginas:</strong> ${pageCount}</p>
          <p><strong>Categorias:</strong> ${categories}</p>
          <p><strong>Idioma:</strong> ${language}</p>
        </div>
      </div>
      <div class="book-detail-body">
        <h3>Descrição</h3>
        <div class="book-description-full">${description}</div>
        <div class="book-actions">
          ${previewLink !== '#' ? `<a href="${previewLink}" target="_blank" class="btn-primary"> Prévia</a>` : ''}
          ${infoLink !== '#' ? `<a href="${infoLink}" target="_blank" class="btn-secondary">ℹ️ Mais Info</a>` : ''}
        </div>
      </div>
    `;
  }

  // Event listeners
  btnSearch.addEventListener('click', () => {
    const query = searchInput.value.trim();
    searchBooks(query);
  });

  searchInput.addEventListener('keypress', (e) => {
    if(e.key === 'Enter'){
      const query = searchInput.value.trim();
      searchBooks(query);
    }
  });

  closeModal.addEventListener('click', () => {
    bookModal.style.display = 'none';
  });

  window.addEventListener('click', (e) => {
    if(e.target === bookModal){
      bookModal.style.display = 'none';
    }
  });
});
